targets.expr:
sum(
  rate(
    {job="k8s-audit", node=~"$node"}
    | json code="responseStatus.code"
    | code !~ "^2"
    [5m]
  )
)

sum(
  rate(
    {job="k8s-audit", node=~"$node"}
    | json code="responseStatus.code"
    | code=~"401|403"
    [5m]
  )
) or vector(0)

topk(10,
  sum by (verb) (
    rate({job="k8s-audit"}
    | json verb="verb", code="responseStatus.code"
    | code=~"401|403"
    [5m])
  )
) or vector(0)

topk(
  10,
  sum by (user)(
    rate(
      {job="k8s-audit", node=~"$node"}
      | json user="user.username", code="responseStatus.code"
      | code=~"401|403"
      [5m]
    )
  )
) or vector(0)

sum by (node) (
  rate(
    {job="k8s-audit", node=~"$node"}
    | json stage="stage", sub="objectRef.subresource", uri="requestURI"
    | stage="ResponseComplete"
    | (sub=~"exec|portforward|attach" or uri=~".*/pods/[^/]+/(exec|portforward|attach).*")
    [5m]
  )
) or vector(0)

sum(
  rate(
    {job="k8s-audit", node=~"$node"}
    | json stage="stage", verb="verb", res="objectRef.resource"
    | stage="ResponseComplete"
    | res=~"roles|rolebindings|clusterroles|clusterrolebindings"
    | verb=~"create|update|patch|delete|deletecollection"
    [5m]
  )
) or vector(0)

sum(
  rate(
    {job="k8s-audit", node=~"$node"}
    | json verb="verb", res="objectRef.resource"
    | res="secrets"
    | verb=~"create|update|patch|delete|deletecollection"
    [5m]
  )
) or vector(0)
